// This is a custom script we use on the client side when we want to make the connection to the server

// Make connection
// Need to change this to whatever link is generated by NGrok
const HOST = 'http://60e3-2600-8807-c1c0-3ea0-1c1d-6aea-ce5f-2e41.ngrok.io'
const socket = io.connect(HOST);

// Query DOM
const message = document.getElementById('message');
const username = document.getElementById('username');
const btn = document.getElementById('send');

const output = document.getElementById('output');
const feedback = document.getElementById('feedback');

// Emitting event when a user clicks send
let userTyping = null;
btn.addEventListener('click', () => {
    socket.emit('chat-message', {
        message: message.value,
        username: username.value
    });

    username.disabled = true;
    message.value = "";
});
message.addEventListener('keypress', () => {
    socket.emit('typing', username.value);
});

// Listen for events from server on frontend
socket.on('chat-message', (data) => {
    output.innerHTML += '<p><strong>' + data.username + ': </strong>' + data.message + '</p>'
    if(userTyping === data.username) {
        feedback.innerHTML = '';
    }
});
socket.on('typing', (data) => {
    userTyping = data;
    feedback.innerHTML = '<p><em>' + data + ' is typing ...</em></p>'
})